import React, { Component } from 'react';
import '../css/tab_css.css';
import tesla from '../images/icons/tesla_icon.jpg';
import lexus from '../images/icons/lexus_icon.png';
import hyundai from '../images/icons/hyundai.jpeg';
import mercedes from '../images/icons/mercedes.jpeg';

const company_name = [
  {
    company: 'Tesla',
    icon_url: tesla,
  },
  {
    company: 'Lexus',
    icon_url: lexus,
  },
  {
    company: 'Hyundai',
    icon_url: hyundai,
  },
  {
    company: 'Mercedes',
    icon_url: mercedes,
  },
];

class Tabs extends Component {
  constructor(props) {
    super(props);
    this.state = { car : 'Tesla' };
  }
  catelog = (name) => {
    console.log(name);
    this.setState({car: 'Tesla'})
  };
  render() {
    // var cat = () => {
    //   console.log(this);
    // };
    return (
      <div>
        <div className="tablet">
          {company_name.map((n) => (
            <div className="tab" onClick={() => this.catelog(n.company)}>
              {/* // check */}
              <img src={n.icon_url} />
            </div>
          ))}
        </div>

<div className='cat'>
  this.state.car
</div>



      </div>
    );
  }
}

export default Tabs;
