import React from 'react';
import Catelog from './Catelog';
// import { data } from '../data';

// var mycatelog;

const carType = [
  {
    company: 'Tesla',
    icon_url: '../images/icons/tesla_icon.jpg',
  },
  {
    company: 'Lexus',
    icon_url:
      'https://cdn.autoportal.com/img/news/16961/main/b31b381818a64dc81f1d7cd82ab89fde/main_cropped.jpg',
  },
  {
    company: 'Hyundai',
    icon_url:
      'https://images.hdqwalls.com/wallpapers/thumb/2021-rolls-royce-ghost-vn.jpg',
  },
  {
    company: 'Sports',
    icon_url:
      'https://images.hdqwalls.com/wallpapers/bthumb/4k-mclaren-720s-vorsteiner-zi.jpg',
  },
];

class Tabs extends React.Component {
  Tabs() {
    // var mycatelog = data.map((cars) => <Catelog cars={cars} type="none" />);
  }
  mycatelog = data.map((cars) => <Catelog cars={cars} type="none" />);
  catelogContent = (modelType) => {
    if (modelType === 'All') {
      mycatelog = data.map((cars) => <Catelog cars={cars} />);
      console.log(modelType);
    } else {
      mycatelog = data.map((cars) => <Catelog cars={cars} type={modelType} />);
      console.log(modelType);
    }
  };

  render() {
    return (
      <div>
        <div className="tab_buttons">
          {carType.map((carType) => (
            <button
              onClick={() => this.catelogContent(carType.type)}
              className="tab"
              style={{ backgroundImage: `url(${carType.icon_url})` }}
            >
              <div className="tab_text">{carType.type}</div>
            </button>
          ))}
        </div>
        <div id="card_lib">{mycatelog}</div>
      </div>
    );
  }
}

export default Tabs;
